import React from 'react';
import { NavLink } from 'react-router-dom';

export default function Overlay(props) {

	function NavLinkStyles ({isActive}){
		console.log(isActive)
		return {
		color : isActive ? '#261B6C'	 : 'red',
		display : isActive ? 'none' : 'none'
		}

	}

  return (
		<div className={`overlay-menu ${props.isOpen ? "" : "active-menu"}`}>
			<ul>
				<li>
					{" "}
					<NavLink style={NavLinkStyle} to="/" onClick={props.toggle}>
						Home
					</NavLink>{" "}
				</li>
				<li>
					{" "}
					<NavLink
						style={NavLinkStyle}
						to="/about"
						onClick={props.toggle}
					>
						About
					</NavLink>{" "}
				</li>
				<li>
					{" "}
					<NavLink
						style={NavLinkStyle}
						to="/products"
						onClick={props.toggle}
					>
						Products
					</NavLink>{" "}
				</li>
				<li>
					{" "}
					<NavLink
						style={NavLinkStyle}
						to="/recipes"
						onClick={props.toggle}
					>
						Recipes
					</NavLink>{" "}
				</li>
				<li>
					{" "}
					<NavLink
						style={NavLinkStyle}
						to="/kids"
						onClick={props.toggle}
					>
						Kid's Corner
					</NavLink>{" "}
				</li>
				<li>
					{" "}
					<NavLink
						style={NavLinkStyle}
						to="/contact"
						onClick={props.toggle}
					>
						Contact Us
					</NavLink>{" "}
				</li>
			</ul>
		</div>
  );
}
