import './App.scss';
import { Routes , Route  } from 'react-router-dom';
import {useState } from 'react'
import Home from './pages/Home';
import About from './pages/About';
import Products from './pages/Products';
import Kids from './pages/Kids';
import Contact from './pages/Contact';
import Recipes from './pages/Recipes';
function App() {
          const [isOpen, setIsOpen] = useState(false);
			function toggle() {
				setIsOpen((prevVAlue) => !prevVAlue);
			}
				const headers = {
					header1: "For Kid's",
					header2: "For Vegans",
					header3: "For Family",
				};
				// / code for the active tab handler
				 const [activeTab, setActiveTab] = useState(1);

					const data = [
						{
							bigimg: "imgs/shwarmabig.png",
							smallimg: "imgs/shwarmasmall.png",
							heading: "Ss",
						},
						{
							bigimg: "imgs/shwarmabig.png",
							smallimg: "imgs/shwarmasmall.png",
							heading: "Sweet ShortBread",
						},
						{
							bigimg: "imgs/shwarmabig.png",
							smallimg: "imgs/shwarmasmall.png",
							heading: "Salmon Strips",
						},
					];
					const [currentData ,setCurrentData] = useState(data[0])

					const handleTabClick = (tabIndex) => {
						setActiveTab(tabIndex);
						setCurrentData(data[tabIndex-1]);
					};


  return (
		<div>
			<Routes>
				<Route
					path="/"
					element={
						<Home	currentData={currentData}
							isOpen={isOpen}
							headers={headers}
							toggle={toggle}
							activeTab={activeTab}
							handleTabClick={handleTabClick}
						/>
					}
				/>
				<Route
					path="/about"
					element={<About isOpen={isOpen} toggle={toggle} />}
				/>
				<Route
					path="/Products"
					element={
						<Products
							isOpen={isOpen}
							headers={headers}
							toggle={toggle}
							activeTab={activeTab}
							handleTabClick={handleTabClick}
						/>
					}
				/>
				<Route
					path="/recipes"
					element={
						<Recipes
							isOpen={isOpen}
							toggle={toggle}
							activeTab={activeTab}
							handleTabClick={handleTabClick}
						/>
					}
				/>
				<Route
					path="/kids"
					element={<Kids isOpen={isOpen} toggle={toggle} />}
				/>
				<Route
					path="/contact"
					element={<Contact isOpen={isOpen} toggle={toggle} />}
				/>
			</Routes>
		</div>
  );
}

export default App;
